#include "textflag.h"

// AVX x1 definitions

#define OUTPUT_PTR	DI
#define DATA_PTR	SI
#define NUM_BLKS	DX
#define TBL		CX

#define RAL AX
#define RBL BX
#define RCL BP
#define RDL R8
#define REL R9
#define RFL R10
#define RGL R11
#define RHL R12

#define XTMP0 X4
#define XTMP1 X5
#define XTMP2 X6
#define XTMP3 X7
#define XTMP4 X8
#define XTMP5 X11
#define XFER X9

#define y0 R13
#define y1 R14
#define y2 R15

#define _SHUF_00BA X10
#define _SHUF_DC00 X12
#define _BYTE_FLIP_MASK X13

#define COPY_XMM_AND_BSWAP(dst,src,msk) \
	VMOVDQU		src, dst; \
	VPSHUFB		msk, dst, dst

#define FOUR_ROUNDS_AND_SCHEDA(a, b, c, d, e, f, g, h, X0_, X1_, X2_, X3_) \
	RORXL	$(25-11), e, y0; \
		VPALIGNR	$4, X2_, X3_, XTMP0; \
	RORXL	$(22-13), a, y1; \
	XORL	e, y0; \
	MOVL	f, y2; \
	RORXL	$(11-6), y0, y0; \
	XORL	a, y1; \
	XORL	g, y2; \
		VPADDD	X0_, XTMP0, XTMP0; \
	XORL	e, y0; \
	ANDL	e, y2; \
	RORXL	$(13-2), y1, y1; \ 
		VPALIGNR	$4, X0_, X1_, XTMP1; \
	XORL	a, y1; \
	RORXL	$6, y0, y0; \ 
	XORL	g, y2; \
	RORXL	$2, y1, y1; \
	ADDL	y0, y2; \
	ADDL	(0*4)(SP), y2; \
	MOVL	a, y0; \
	ADDL	y2, h; \
	MOVL	a, y2; \
		VPSRLD	$7, XTMP1, XTMP2; \
	ORL	c, y0; \
	ADDL	h, d; \
	ANDL	c, y2; \
		VPSLLD	$(32-7), XTMP1, XTMP3; \
	ANDL	b, y0; \
	ADDL	y1, h; \
		VPOR	XTMP2, XTMP3, XTMP3; \
	ORL	y2, y0; \
	ADDL	y0, h

#define FOUR_ROUNDS_AND_SCHEDB(a, b, c, d, e, f, g, h, X0_, X1_, X2_, X3_) \
	RORXL	$(25-11), e, y0; \
	RORXL	$(22-13), a, y1; \
	XORL	e, y0; \
		VPSRLD	$18, XTMP1, XTMP2; \
	MOVL	f, y2; \
	RORXL	$(11-6), y0, y0; \
	XORL	a, y1; \
	XORL	g, y2; \
		VPSRLD	$3, XTMP1, XTMP4; \
	XORL	e, y0; \
	ANDL	e, y2; \
	RORXL	$(13-2), y1, y1; \
	XORL	a, y1; \
	RORXL	$6, y0, y0; \
		VPSLLD	$(32-18), XTMP1, XTMP1; \
	XORL	g, y2; \
	RORXL	$2, y1, y1; \
		VPXOR	XTMP1, XTMP3, XTMP3; \
	ADDL	y0, y2; \
	ADDL	(1*4)(SP), y2; \
	MOVL	a, y0; \
		VPXOR	XTMP2, XTMP3, XTMP3; \
	ADDL	y2, h; \
	MOVL	a, y2; \
	ORL	c, y0; \
		VPXOR	XTMP4, XTMP3, XTMP1; \
	ADDL	h, d; \
	ANDL	c, y2; \
		VPSHUFD $0xFA, X3_, XTMP2; \
	ANDL	b, y0; \
	ADDL	y1, h; \
		VPADDD	XTMP1, XTMP0, XTMP0; \
	ORL	y2, y0; \
	ADDL	y0, h


#define FOUR_ROUNDS_AND_SCHEDC(a, b, c, d, e, f, g, h, X0_, X1_, X2_, X3_) \
	RORXL	$(25-11), e, y0; \
	RORXL	$(22-13), a, y1; \
	XORL	e, y0; \
		VPSRLD	$10, XTMP2, XTMP4; \
	MOVL	f, y2; \
	RORXL	$(11-6), y0, y0; \
	XORL	a, y1; \
		VPSRLQ	$19, XTMP2, XTMP3; \
	XORL	g, y2; \
	XORL	e, y0; \
	ANDL	e, y2; \
		VPSRLQ	$17, XTMP2, XTMP2; \
	RORXL	$(13-2), y1, y1; \
	XORL	a, y1; \
	RORXL	$6, y0, y0; \
		VPXOR	XTMP3, XTMP2, XTMP2; \
	XORL	g, y2; \
	RORXL	$2, y1, y1; \
	ADDL	y0, y2; \
		VPXOR	XTMP2, XTMP4, XTMP4; \
	ADDL	(2*4)(SP), y2; \
	MOVL	a, y0; \
	ADDL	y2, h; \
		VPSHUFB	 _SHUF_00BA, XTMP4, XTMP4; \
	MOVL	a, y2; \
	ORL	c, y0; \
	ADDL	h, d; \
		VPADDD	XTMP4, XTMP0, XTMP0; \
	ANDL	c, y2; \
	ANDL	b, y0; \
		VPSHUFD $0x50, XTMP0, XTMP2; \
	ADDL	y1, h; \
	ORL	y2, y0; \
	ADDL	y0, h

#define FOUR_ROUNDS_AND_SCHEDD(a, b, c, d, e, f, g, h, X0_, X1_, X2_, X3_) \
	RORXL	$(25-11), e, y0; \
	RORXL	$(22-13), a, y1; \
		VPSRLD	$10, XTMP2, XTMP5; \
	XORL	e, y0; \
	MOVL	f, y2; \
	RORXL	$(11-6), y0, y0; \
		VPSRLQ  $19, XTMP2, XTMP3; \
	XORL	a, y1; \
	XORL	g, y2; \
	XORL	e, y0; \
		VPSRLQ $17, XTMP2, XTMP2; \
	ANDL	e, y2; \
	RORXL	$(13-2), y1, y1; \
	XORL	a, y1; \
		VPXOR	XTMP3, XTMP2, XTMP2; \
	RORXL	$6, y0, y0; \
	XORL	g, y2; \
	RORXL	$2, y1, y1; \
		VPXOR	XTMP2, XTMP5, XTMP5; \
	ADDL	y0, y2; \
	ADDL	(3*4)(SP), y2; \
	MOVL	a, y0; \
	ADDL	y2, h; \
	MOVL	a, y2; \
		VPSHUFB	 _SHUF_DC00, XTMP5, XTMP5; \
	ORL	c, y0; \
	ADDL	h, d; \
	ANDL	c, y2; \
		VPADDD	XTMP0, XTMP5, X0_; \
	ANDL	b, y0; \
	ADDL	y1, h; \
	ORL	y2, y0; \
	ADDL	y0, h

#define FOUR_ROUNDS_AND_SCHED(a, b, c, d, e, f, g, h, X0_, X1_, X2_, X3_) \
	FOUR_ROUNDS_AND_SCHEDA(a, b, c, d, e, f, g, h, X0_, X1_, X2_, X3_); \
	FOUR_ROUNDS_AND_SCHEDB(h, a, b, c, d, e, f, g, X0_, X1_, X2_, X3_); \
	FOUR_ROUNDS_AND_SCHEDC(g, h, a, b, c, d, e, f, X0_, X1_, X2_, X3_); \
	FOUR_ROUNDS_AND_SCHEDD(f, g, h, a, b, c, d, e, X0_, X1_, X2_, X3_)

#define DO_ROUND(base, offset, a, b, c, d, e, f, g, h) \
	RORXL	$(25-11), e, y0; \
	RORXL	$(22-13), a, y1; \
	XORL	e, y0; \
	MOVL	f, y2; \
	RORXL	$(11-6), y0, y0; \
	XORL	a, y1; \
	XORL	g, y2; \
	XORL	e, y0; \
	ANDL	e, y2; \
	RORXL	$(13-2), y1, y1; \
	XORL	a, y1; \
	RORXL	$6, y0, y0; \
	XORL	g, y2; \
	RORXL	$2, y1, y1; \
	ADDL	y0, y2; \
	ADDL	(offset)(base), y2; \
	MOVL	a, y0; \
	ADDL	y2, h; \
	MOVL	a, y2; \
	ORL	c, y0; \
	ADDL	h, d; \
	ANDL	c, y2; \
	ANDL	b, y0; \
	ADDL	y1, h; \
	ORL	y2, y0; \
	ADDL	y0, h

// AVX x4 definitions

#define XA0	X8
#define XA1	X9
#define XA2	X10

#define XT0	X14
#define XT1	X13
#define XT2	X12
#define XT3	X11
#define XT4	X10
#define XT5	X9
#define TMP4	X15 

#define TRANSPOSE_4_U32(r0, r1, r2, r3, t0, t1) \
	VSHUFPS $0x44, r1, r0, t0; \
	VSHUFPS $0xEE, r1, r0, r0; \
	VSHUFPS $0x44, r3, r2, t1; \
	VSHUFPS $0xEE, r3, r2, r2; \
	VSHUFPS $0xDD, t1, t0, r1; \
	VSHUFPS $0xDD, r2, r0, r3; \
	VSHUFPS $0x88, r2, r0, r0; \
	VSHUFPS $0x88, t1, t0, t0

#define PRORD4(src, imm) \
	VPSLLD	$(32 - imm), src, TMP4; \
	VPSRLD  $imm, src, src; \
	VPOR	TMP4, src, src

#define PRORD4_nd(dst, src, amt) \
	VPSLLD $(32 - amt), src, TMP4; \
	VPSRLD $amt, src, dst; \
	VPOR   TMP4, dst, dst

#define ROUND4_00_15_PADD(a, b, c, d, e, f, g, h, T1, i) \
	PRORD4_nd(XA0, e, 5); \
	VPXOR	g, f, XA2; \
	VPAND	e, XA2, XA2; \
	VPXOR	g, XA2, XA2; \
	PRORD4_nd(XA1, e, 25); \
	VMOVDQU	(64*i)(TBL), T1; \
	VPXOR	e, XA0, XA0; \
	PRORD4(XA0, 6); \
	VPADDD	XA2, h, h; \
	PRORD4_nd(XA2, a, 11); \
	VPADDD	T1, h, h; \
	VPXOR	XA1, XA0, XA0; \
	PRORD4_nd(XA1, a, 22); \
	VPXOR	c, a, T1; \
	VPAND	b, T1, T1; \
	VPADDD	XA0, h, h; \
	VPADDD	h, d, d; \
	VPXOR	a, XA2, XA2; \
	PRORD4(XA2, 2); \
	VPXOR	XA1, XA2, XA2; \
	VPAND	c, a, XA1; \
	VPOR	T1, XA1, XA1; \
	VPADDD	XA1, h, h; \
	VPADDD	XA2, h, h

#define ROUND4_00_15(a, b, c, d, e, f, g, h, T1, i) \
	PRORD4_nd(XA0, e, 5); \
	VPXOR	g, f, XA2; \
	VPAND	e, XA2, XA2; \
	VPXOR	g, XA2, XA2; \
	PRORD4_nd(XA1, e, 25); \
	VMOVDQU	T1, (16*(i&0xf))(SP); \
	VPADDD	(64*i)(TBL), T1, T1; \
	VPXOR	e, XA0, XA0; \
	PRORD4(XA0, 6); \
	VPADDD	XA2, h, h; \
	PRORD4_nd(XA2, a, 11); \
	VPADDD	T1, h, h; \
	VPXOR	XA1, XA0, XA0; \
	PRORD4_nd(XA1, a, 22); \
	VPXOR	c, a, T1; \
	VPAND	b, T1, T1; \
	VPADDD	XA0, h, h; \
	VPADDD	h, d, d; \
	VPXOR	a, XA2, XA2; \
	PRORD4(XA2, 2); \
	VPXOR	XA1, XA2, XA2; \
	VPAND	c, a, XA1; \
	VPOR	T1, XA1, XA1; \
	VPADDD	XA1, h, h; \
	VPADDD	XA2, h, h

#define ROUND4_16_XX(a, b, c, d, e, f, g, h, T1, i) \
	VMOVDQU	(16*((i-15)&0x0f))(SP), T1; \
	VMOVDQU	(16*((i-2)&0x0f))(SP), XA1; \
	VMOVDQA	T1, XA0; \
	PRORD4(T1, 11); \
	VMOVDQA	XA1, XA2; \
	PRORD4(XA1, 2); \
	VPXOR	XA0, T1, T1; \
	PRORD4(T1, 7); \
	VPXOR	XA2, XA1, XA1; \
	PRORD4(XA1, 17); \
	VPSRLD	$3, XA0, XA0; \
	VPXOR	XA0, T1, T1; \
	VPSRLD	$10, XA2, XA2; \
	VPXOR	XA2, XA1, XA1; \
	VPADDD	(16*((i-16)&0x0f))(SP), T1, T1; \
	VPADDD	(16*((i-7)&0x0f))(SP), XA1, XA1; \
	VPADDD	XA1, T1, T1; \
	ROUND4_00_15(a, b, c, d, e, f, g, h, T1, i)


// AVX2 x8 definitions

#define a0 Y12
#define a1 Y13
#define a2 Y14
#define TMP Y15
#define TMP0 Y6
#define TMP1 Y7
#define TT0 Y8
#define TT1 Y9
#define TT2 Y10
#define TT3 Y11
#define TT4 Y12
#define TT5 Y13
#define TT6 Y14
#define TT7 Y15

#define _DIGEST	512
#define _YTMP	768

#define YTMP0	_YTMP + 0*32
#define YTMP1	_YTMP + 1*32
#define YTMP2	_YTMP + 2*32
#define YTMP3	_YTMP + 3*32

#define TRANSPOSE8_U32_LOAD8(offset) \
	VMOVUPS	(offset + 0*64)(DATA_PTR), TT0; \
	VMOVUPS	(offset + 1*64)(DATA_PTR), TT1; \
	VMOVUPS	(offset + 2*64)(DATA_PTR), TT2; \
	VMOVUPS	(offset + 3*64)(DATA_PTR), TT3; \
	VMOVUPS	(offset + 0*64+16)(DATA_PTR), TT4; \
	VMOVUPS	(offset + 1*64+16)(DATA_PTR), TT5; \
	VMOVUPS	(offset + 2*64+16)(DATA_PTR), TT6; \
	VMOVUPS	(offset + 3*64+16)(DATA_PTR), TT7; \
	VINSERTI128 $0x01, (offset + 4*64)(DATA_PTR), TT0, TT0; \
	VINSERTI128 $0x01, (offset + 5*64)(DATA_PTR), TT1, TT1; \
	VINSERTI128 $0x01, (offset + 6*64)(DATA_PTR), TT2, TT2; \
	VINSERTI128 $0x01, (offset + 7*64)(DATA_PTR), TT3, TT3; \
	VINSERTI128 $0x01, (offset + 4*64+16)(DATA_PTR), TT4, TT4; \
	VINSERTI128 $0x01, (offset + 5*64+16)(DATA_PTR), TT5, TT5; \
	VINSERTI128 $0x01, (offset + 6*64+16)(DATA_PTR), TT6, TT6; \
	VINSERTI128 $0x01, (offset + 7*64+16)(DATA_PTR), TT7, TT7

#define TRANSPOSE8_U32_PRELOADED \
	VSHUFPS $0x44, TT1, TT0, TMP0; \
	VSHUFPS $0xEE, TT1, TT0, TT0; \
	VSHUFPS $0x44, TT3, TT2, TMP1; \
	VSHUFPS $0xEE, TT3, TT2, TT2; \
	VSHUFPS $0xDD, TMP1, TMP0, TT1; \
	VSHUFPS $0xDD, TT2, TT0, TT3; \
	VSHUFPS $0x88, TT2, TT0, TT2; \
	VSHUFPS $0x88, TMP1, TMP0, TT0; \
	VSHUFPS $0x44, TT5, TT4, TMP0; \
	VSHUFPS $0xEE, TT5, TT4, TT4; \
	VSHUFPS $0x44, TT7, TT6, TMP1; \
	VSHUFPS $0xEE, TT7, TT6, TT6; \
	VSHUFPS $0xDD, TMP1, TMP0, TT5; \
	VSHUFPS $0xDD, TT6, TT4, TT7; \
	VSHUFPS $0x88, TT6, TT4, TT6; \
	VSHUFPS $0x88, TMP1, TMP0, TT4

#define TRANSPOSE8_U32 \
       VSHUFPS $0x44, Y1, Y0, TT0; \
       VSHUFPS $0xEE, Y1, Y0, Y0; \
       VSHUFPS $0x44, Y3, Y2, TT1; \
       VSHUFPS $0xEE, Y3, Y2, Y2; \
       VSHUFPS $0xDD, TT1, TT0, Y3; \
       VSHUFPS $0x88, Y2, Y0, Y1; \
       VSHUFPS $0xDD, Y2, Y0, Y0; \
       VSHUFPS $0x88, TT1, TT0, TT0; \
       VSHUFPS $0x44, Y5, Y4, Y2; \
       VSHUFPS $0xEE, Y5, Y4, Y4; \
       VSHUFPS $0x44, Y7, Y6, TT1; \
       VSHUFPS $0xEE, Y7, Y6, Y6; \
       VSHUFPS $0xDD, TT1, Y2, Y7; \
       VSHUFPS $0x88, Y6, Y4, Y5; \
       VSHUFPS $0xDD, Y6, Y4, Y4; \
       VSHUFPS $0x88, TT1, Y2, TT1; \
       VPERM2F128 $0x13, Y1, Y5, Y6; \
       VPERM2F128 $0x02, Y1, Y5, Y2; \
       VPERM2F128 $0x13, Y3, Y7, Y5; \
       VPERM2F128 $0x02, Y3, Y7, Y1; \
       VPERM2F128 $0x13, Y0, Y4, Y7; \
       VPERM2F128 $0x02, Y0, Y4, Y3; \
       VPERM2F128 $0x13, TT0, TT1, Y4; \
       VPERM2F128 $0x02, TT0, TT1, Y0

#define PRORD(src, imm) \
	VPSLLD	$(32 - imm), src, TMP; \
	VPSRLD  $imm, src, src; \
	VPOR	TMP, src, src

#define PRORD_nd(dst, src, amt) \
	VPSLLD $(32 - amt), src, TMP; \
	VPSRLD $amt, src, dst; \
	VPOR   TMP, dst, dst

#define ROUND_00_15_PADD(a, b, c, d, e, f, g, h, T1, i) \
	PRORD_nd(a0, e, 5); \
	VPXOR	g, f, a2; \
	VPAND	e, a2, a2; \
	VPXOR	g, a2, a2; \
	PRORD_nd(a1, e, 25); \
	VMOVDQU	(64*i)(TBL), T1; \
	VPXOR	e, a0, a0; \
	PRORD(a0, 6); \
	VPADDD	a2, h, h; \
	PRORD_nd(a2, a, 11); \
	VPADDD	T1, h, h; \
	VPXOR	a1, a0, a0; \
	PRORD_nd(a1, a, 22); \
	VPXOR	c, a, T1; \
	VPAND	b, T1, T1; \
	VPADDD	a0, h, h; \
	VPADDD	h, d, d; \
	VPXOR	a, a2, a2; \
	PRORD(a2, 2); \
	VPXOR	a1, a2, a2; \
	VPAND	c, a, a1; \
	VPOR	T1, a1, a1; \
	VPADDD	a1, h, h; \
	VPADDD	a2, h, h


#define ROUND_00_15(a, b, c, d, e, f, g, h, T1, i) \
	PRORD_nd(a0, e, 5); \
	VPXOR	g, f, a2; \
	VPAND	e, a2, a2; \
	VPXOR	g, a2, a2; \
	PRORD_nd(a1, e, 25); \
	VMOVDQU	T1, (32*(i&0xf))(SP); \
	VPADDD	(64*i)(TBL), T1, T1; \
	VPXOR	e, a0, a0; \
	PRORD(a0, 6); \
	VPADDD	a2, h, h; \
	PRORD_nd(a2, a, 11); \
	VPADDD	T1, h, h; \
	VPXOR	a1, a0, a0; \
	PRORD_nd(a1, a, 22); \
	VPXOR	c, a, T1; \
	VPAND	b, T1, T1; \
	VPADDD	a0, h, h; \
	VPADDD	h, d, d; \
	VPXOR	a, a2, a2; \
	PRORD(a2, 2); \
	VPXOR	a1, a2, a2; \
	VPAND	c, a, a1; \
	VPOR	T1, a1, a1; \
	VPADDD	a1, h, h; \
	VPADDD	a2, h, h

#define ROUND_16_XX(a, b, c, d, e, f, g, h, T1, i) \
	VMOVDQU	(32*((i-15)&0x0f))(SP), T1; \
	VMOVDQU	(32*((i-2)&0x0f))(SP), a1; \
	VMOVDQA	T1, a0; \
	PRORD(T1, 11); \
	VMOVDQA	a1, a2; \
	PRORD(a1, 2); \
	VPXOR	a0, T1, T1; \
	PRORD(T1, 7); \
	VPXOR	a2, a1, a1; \
	PRORD(a1, 17); \
	VPSRLD	$3, a0, a0; \
	VPXOR	a0, T1, T1; \
	VPSRLD	$10, a2, a2; \
	VPXOR	a2, a1, a1; \
	VPADDD	(32*((i-16)&0x0f))(SP), T1, T1; \
	VPADDD	(32*((i-7)&0x0f))(SP), a1, a1; \
