	TRANSPOSE_4_U32(X0, X1, X2, X3, XT0, XT1)
	TRANSPOSE_4_U32(X4, X5, X6, X7, XT2, XT1)
	VMOVDQU	_PSHUFFLE_BYTE_FLIP_MASK_16<>(SB), TMP4
